mapper.select:
select rt.rn, rt.no, rt.title, rt.hit, rt.regDate, rt.userNo, rt.name from (select rownum rn, ot.no, ot.title, ot.hit, ot.regDate, ot.userNo, ot.name from (select b.no, b.title, b.hit, b.reg_date regDate, b.user_no userNo, u.name from board b, users u where b.user_no = u.no
and b.title like '%'|| #{keyword} ||'%'
order by b.no desc)ot )rt where rn>=#{startRnum} and rn<=#{endRnum}

mapper.update:

	update board
	set hit = hit+1
	where no = #{no}


	update board 
   	set title=#{title },
       	content=#{content }
 	where no=#{no }
  	and user_no = #{userNo}
